import React, { Fragment ,useState} from 'react';
import { MdExpandMore ,MdExpandLess } from "react-icons/md";
import { motion } from "framer-motion";
import { portfolioData } from '../portfolioData';
import Button from './Button'

const iconVariants ={
    opened:{
       scale:1,
        opcity:1,
        transition: {
          type: "spring",
          stiffness: 20,
          restDelta: 2
        }
      },
    closed:{
      scale:1,
        opcity:1,
        transition: {
          type: "spring",
          stiffness: 400,
          damping: 40
        }
      }
    }


const Portfolio =({portfolioData})=>{
const [isOpen, setisOpen] = useState(false);

const handleClick = (e) =>{
  setisOpen(!isOpen)
  console.log(e.target.value)
}

    return (

        <div className="container portfolio"> 
        {portfolioData.map((p,index)=>
        <div className="card" key={index} >
       <h3>Projects:</h3>
       <div className="vid">
       <iframe frameborder="0" type="text/html" src={p.vidUrl} width="100%" height="100%" allowfullscreen allow="autoplay"> </iframe>
      </div>
      <div className="btns">
        <motion.a className="btn visit-btn" target="_blank" href={p.websiteUrl}>Visit Website</motion.a>
        <a className="btn github-btn" target="_blank" href={p.githubUrl}>Github</a>
        </div>

       <Button variants={iconVariants}  animate={isOpen?"opened":"closed"} tools={p.tools}/>


    </div>
    )}
</div> 

    )
}

export default Portfolio;
