import React, { Fragment ,useEffect} from 'react'
import ContantForm from './ContantForm'
import leftSideImg from './media/left-side.png'
import {useInView} from 'react-intersection-observer'
import {motion,useAnimation ,AnimatePresence} from 'framer-motion'


const subVariants ={
    hidden:{
      opacity:0,
    },
    visible:{
      opacity:1,
     transition:{
      staggerChildren:.9,
      delayChildren:1.5,
      type:'spring',
      ease:"easeInOut",
     }
    }
}


const sideLVariants ={
    hidden:{
        y:"50vh",
    },
    "visible":{
      y:0,
        transition:{
            ease:"easeInOut",
            duration:.5,
        }
    }
}

const sideRVariants ={
    hidden:{
        y:"20vh",
    },
    "visible":{
      y:0,
        transition:{
            ease:"easeInOut",
            duration:.5,
        }
    }
}



const ContantMe = () => {

    const animation = useAnimation();
    const [ref,inView,entry]=useInView({threshold:0});

    useEffect(() => {
        inView ? animation.start('visible') :
            animation.start('hidden');
    }, [animation, inView])


    return (
        <div className="container contact-me">
        <AnimatePresence>
        <motion.div  className="subcontainer" variants={subVariants} animate={animation} initial="hidden" ref={ref}>
        <motion.div className='left-side' 
          variants={sideLVariants}
        initial="hidden"
        animate={animation}
        ref={ref}>
        <h1>Hi ;</h1>
        <p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum
         has been the industry's standard dummy text ever since the 1500s, when an unknown
         printer took a galley of type and scrambled it to make a type specimen book. It 
         has survived not only five centuries, but also the leap into electronic typesetting, 
         remaining essentially unchanged. It was popularised in the 1960s with the release 
         of Letraset sheets containing Lorem Ipsum passages .</p>
        </motion.div>
        <motion.div className='right-side'  variants={sideRVariants} initial="hidden"
        animate={animation}
        ref={ref}>
        <ContantForm />
        </motion.div>
        </motion.div>
        </AnimatePresence>
        <img src={leftSideImg} alt="abstract"/>
        </div>
    )
}

export default ContantMe
